import React, { useState } from 'react';
import { useRoutes } from 'react-router-dom';
import DataEntryForm from './DataEntryForm';
import DataSearch from './DataSearch';

const CombinedDataPage = () => {
  const [searchQuery, setSearchQuery] = useState('');

  const handleSearch = (query) => {
    setSearchQuery(query);
  };

  const routes = useRoutes([
    {
      path: '/',
      element: (
        <div className="combined-data-page">
          <header>
            <h1>Infinicue Solutions Data</h1>
            <nav>
            </nav>
          </header>
          <section className="data-entry-form">
            <DataEntryForm searchQuery={searchQuery} />
          </section>
          <section className="data-search">
            <DataSearch onSearch={handleSearch} />
          </section>
          {/* Show only data-preview-container when printing */}
          <section className="data-preview-container">
            {/* Content of data-preview-container */}
          </section>
        </div>
      ),
    },
  ]);

  return (
    <>
      {routes}
      {/* Add inline style to hide everything except data-preview-container when printing */}
      <style>
        {`
          @media print {
            .combined-data-page *:not(.data-preview-container) {
              display: none !important;
            }
          }
        `}
      </style>
    </>
  );
};

export default CombinedDataPage;
