import React, { useState } from 'react';
import axios from 'axios';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faSearch } from '@fortawesome/free-solid-svg-icons';
import './DataSearch.css';

const DataSearch = () => {
  const [searchQuery, setSearchQuery] = useState('');
  const [searchResult, setSearchResult] = useState(null);
  const [error, setError] = useState(null);

  const handleSearch = async () => {
    try {
      const response = await axios.get(`http://localhost:5000/api/search-data/${searchQuery}`);
      setSearchResult(response.data);
      setError(null);
    } catch (error) {
      console.error('Error searching data:', error);
      setSearchResult(null);
      setError('Error searching data. Please try again.');
    }
  };

  const renderSearchResult = () => {
    if (!searchResult || searchResult.length === 0) {
      return <div className="no-data-message">No matching data found for the entered query.</div>;
    }

    const product = searchResult[0];
    const productDescriptionIndex = product ? product.length - 1 : -1;

    return (
      <div className="data-preview-container">
        <div className="product-description-container">
          <strong>PRODUCT DESCRIPTION:</strong>
          <p>{product ? product[productDescriptionIndex] || 'N/A' : 'N/A'}</p>
        </div>
        <div className="barcode-container">
          {product && product[10] && <img src={product[10]} alt="Barcode" className="barcode-image image-preview" />}
        </div>
        <div className="qrcode-container">
          {product && product[11] && <img src={product[11]} alt="QR Code" className="qrcode-image image-preview" />}
        </div>
      </div>
    );
  };

  return (
    <div className="data-search-container">
      <div className="search-section">
        <label className="search-label">
          <input
            type="text"
            className="search-input"
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
          />
        </label>
        <button className="search-button" onClick={handleSearch}>
          <FontAwesomeIcon icon={faSearch} />
        </button>
      </div>

      {error && <div className="error-message">{error}</div>}

      {renderSearchResult()}
    </div>
  );
};

export default DataSearch;
