import React, { useState } from 'react';
import axios from 'axios';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faSearch, faPrint } from '@fortawesome/free-solid-svg-icons';
import './DataSearch.css';

const DataSearch = () => {
  const [searchQuery, setSearchQuery] = useState('');
  const [searchResult, setSearchResult] = useState(null);
  const [error, setError] = useState(null);

  const handleSearch = async () => {
    try {
      const response = await axios.get(`http://localhost:5000/api/search-data/${searchQuery}`);
      setSearchResult(response.data);
      setError(null);
    } catch (error) {
      console.error('Error searching data:', error);
      setSearchResult(null);
      setError('Error searching data. Please try again.');
    }
  };

  const productDescriptionIndex = searchResult ? searchResult[0].length - 1 : -1;

  const handlePrint = () => {
    const dataPreviewContainer = document.querySelector('.data-preview-container');
    if (dataPreviewContainer) {
      const clonedContainer = dataPreviewContainer.cloneNode(true);
      const printWindow = window.open('', '_blank');
      printWindow.document.body.innerHTML = clonedContainer.outerHTML;
      printWindow.print();
    }
  };

  return (
    <div className="data-search-container">
      <div className="search-section">
        <label className="search-label">
          <input
            type="text"
            className="search-input"
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
          />
        </label>
        <button className="search-button" onClick={handleSearch}>
          <FontAwesomeIcon icon={faSearch} />
        </button>
      </div>

      {error && <div className="error-message">{error}</div>}

      {searchResult && searchResult.length > 0 && (
        <div className="search-results shifted-container">
          <ul className="result-list">
            {/* Render search results here */}
          </ul>
        </div>
      )}

      {searchResult && searchResult.length > 0 && (
        <div className="data-preview-container">
          {/* Render data preview content here */}
        </div>
      )}

      <div className="print-section">
        <button className="print-button" onClick={handlePrint}>
          <FontAwesomeIcon icon={faPrint} />
          Print Preview
        </button>
      </div>
    </div>
  );
};

export default DataSearch;
